//声明actions ： 描述了数据从应用到store的有效信息负载，是修改store的唯一途径。
//要通过action来修改数据，必须把action提交给dispatch，通过diapatch来调用action从而改变数据
const ADD_TYPE = "ADD"
const MINUS_TYPE = "MINUS"
const CITY_TYPE = "CHOOSECITY"

//操作counter的action
export function add(step) {
    return {
        type: ADD_TYPE,
        step: step
    }
}
export function minus() {
    return {
        type: MINUS_TYPE
    }
}

