import axios from 'axios';

const SET_DATA_LIST = "SETDATALIST"

export function setDataList(list,total,currentList){
    return{
        type: SET_DATA_LIST,
        list,
        total,
        currentList
    }
}

export function getDataList() {
    return (dispatch,getState) =>{
        let result = axios.get('productList.json')
        result.then(res => {
            const {data} = res
            const {pageSize,current} = getState().product
            const currentList = data.slice((current-1)*pageSize,current*pageSize)
            dispatch(setDataList(data,data.length,currentList))
        })
    }
}

