/**
 * 初始化数据
 */
let obj = {
    currentList:[],//当前页的数据
    totalData: [],//请求到的全部数据
    current: 1, //当前页码
    pageSize: 10, //每页显示的条数
    total: 0,//商品列表总条数
}

export default function product(state = obj, action) {
    switch (action.type) {
        case "ADD":
            return state + action.step;
        case "MINUS":
            return state - 1;
        default:
            return state;
    }
}
