import axios from 'axios';
import ExportTypography from 'antd/lib/typography/Typography';

const SET_DATA_LIST = "SETDATALIST"
const GET_LIST_BYID = "GETLISTBYID"
const GET_LIST_BUNAME = "GETLISTBUNAME"
const GET_LIST = "GETLIST"

export function setDataList(list,total){
    return{
        type: SET_DATA_LIST,
        list,
        total
    }
}

export function getDataList() {
    return (dispatch,getState) =>{
        let result = axios.get('productList.json')
        result.then(res => {
            const {data} = res
            const {pageSize,current} = getState().product
            // const currentList = data.slice((current-1)*pageSize,current*pageSize)
            dispatch(setDataList(data,data.length))
        })
    }
}
export function getList(number=1,name=""){
    return{
        type: GET_LIST,
        number,
        name
    }
}

export function getListByPage(pageNumber){
    return{
        type: GET_LIST_BYID,
        pageNumber
    }
}

export function getListByName(name){
    return{
        type:GET_LIST_BUNAME,
        name
    }
}
