/**
 * 初始化数据
 */
let obj = {
    currentList:[],//当前页的数据
    totalData: [],//请求到的全部数据
    current: 1, //当前页码
    pageSize: 10, //每页显示的条数
    total: 0,//商品列表总条数
}

export default function product(state = obj, action) {
    switch (action.type) {
        case "SETDATALIST":
            return {
                ...state,
                totalData:action.list,
                total:action.total,
                currentList: action.currentList
            };
        case "GETLISTBYID":
            const {totalData,pageSize} = state
            return {
                ...state,
                currentList: totalData.slice((action-1)*pageSize,action*pageSize)
            }
        default:
            return obj;
    }
}
