import React from 'react';
import './index.css'
//引入actions
import { getDataList } from '@/actions'

class List extends React.Component{
    constructor(props){
        super(props)
    }
    componentWillMount(){
        this.props.getDataList()
    }
    render(){
        const {currentList,} = this.props;
        console.log(currentList,'zhangaiujeaios')
        return(
            <div> 
                {
                    currentList.map(item =>
                        <div className="list-content" key={item.id}>
                            <span> {item.id} </span>
                            <span> {item.name} </span>
                            <span> ￥{item.price} </span>
                            <span className="edit" onClick={() => this.showEditDialog(item.id,item.price)}> 编辑 </span>
                        </div>
                    )
                }
            </div>
        )
    }
}

//mapStatetoProps返回值：可以将state中的数据映射到组件的props中
const mapStatetoProps = state => {
    return {
      product: state.product
    }

  }

  //mapDispatchToProps：可以将action映射到props中(此时的action已经具备了dispatch的功能，无需再通过store.dispatch()来触发)
  const mapDispatchToProps = {
    getDataList
  }

export default connect(mapStatetoProps, mapDispatchToProps)(List);
