import React from 'react';
import { connect } from 'react-redux';
import './index.css'
import { Modal,Input, Checkbox} from 'antd';
//引入actions
import { getDataList,changePrice,changeChecked } from '@/actions'

class List extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            visible: false,
            currentPrice:0,
            currentId:0,
            changeList:[],
        }
    }
    componentWillMount(){
        this.props.getDataList()
    }

    showEditDialog = (id,price) => {
        this.setState({
            visible: true,
            currentPrice: price,
            currentId: id
        });
    }

    handleOk(){
        const {currentPrice,currentId} = this.state
        this.props.changePrice(currentId,currentPrice)
        this.setState({
            visible: false,
        });
    };

    /**
     *关闭弹框,设置弹框隐藏
     *
     * @memberof ProductList
     */
    handleCancel = e => {
        this.setState({
          visible: false,
        });
    };

    /**
     *监听文本框的改变
     *
     * @memberof ProductList
     */
    inputChange = e => this.setState({currentPrice:e.target.value})

    changeCheckBox = (id,e) =>{
        const obj = {
            id,
            checked:e.target.checked
        }
        const changeList = this.state.changeList
        console.log(changeList)
        if(changeList.length == 0){
            console.log('长度为0')
            this.setState({
                changeList: [...changeList,obj]
            },() =>{
                console.log(this.state.changeList)
            })
            return
        }
        changeList.forEach(item =>{
            if(item.id == id){
                item.checked = e.target.checked
                return
            }
        })  
        console.log('888888')   
        // this.setState({
        //     changeList: [...changeList,obj]
        // },() =>{
        //     console.log(this.state.changeList)
        // })
    }

    render(){
        const {currentList} = this.props.product;
        return(
            <div> 
                {
                    currentList.map(item =>
                        <div className="list-content" key={item.id}>
                            <Checkbox onChange={(e) => this.changeCheckBox(item.id,e)}> </Checkbox>
                            <p> {item.id} </p>
                            <p> {item.name} </p>
                            <p> ￥{item.price} </p>
                            <p className="edit" onClick={() => this.showEditDialog(item.id,item.price)}> 编辑 </p>
                        </div>
                    )
                }
                <Modal title="修改商品的价格" visible={this.state.visible} onOk={()=>this.handleOk()} onCancel={this.handleCancel}>
                    <Input value={this.state.currentPrice} onChange={this.inputChange}/>
                </Modal>
            </div>
        )
    }
}

const mapStatetoProps = state => {
    return {
      product: state.product
    }
}

const mapDispatchToProps = {
    getDataList,changePrice,changeChecked
}

export default connect(mapStatetoProps, mapDispatchToProps)(List);
