/**
 * 初始化数据
 */
let obj = {
    currentList:[],//当前页的数据
    totalData: [],//请求到的全部数据
    current: 1, //当前页码
    pageSize: 10, //每页显示的条数
    total: 0,//商品列表总条数
}

export default function product(state = obj, action) {
    switch (action.type) {
        case "SETDATALIST":
            return {
                ...state,
                totalData:action.list,
                total:action.total,
            };
        case "GETLISTBYID":
            const {totalData,pageSize} = state
            const number = action.pageNumber
            return {
                ...state,
                currentList: totalData.slice((number-1)*pageSize,number*pageSize)
            }
        case "GETLISTBUNAME":
            // console.log(state.totalData,'ppppppp')
            return {
                ...state,
                currentList: state.totalData.filter(item => item.name.indexOf(action.name) > -1)
            }
        case "GETLIST" :
            console.log(action,'dsjfbeyrfbeufbefbefb')
            let list = state.totalData.filter(item => item.name.indexOf(action.name) > -1)
            list = list.slice((action.pageNumber-1)*state.pageSize,action.pageNumber*state.pageSize)
            return {
                ...state,
                currentList: list,
                total:list.length
            }
        default:
            return obj;
    }
}
