import React from 'react';
import { connect } from 'react-redux';
import './index.css'
import { Modal,Input } from 'antd';
//引入actions
import { getDataList } from '@/actions'

class List extends React.Component{
    constructor(props){
        super(props)
    }
    componentWillMount(){
        this.props.getDataList()
    }
    render(){
        const {currentList} = this.props.product;
        return(
            <div> 
                {
                    currentList.map(item =>
                        <div className="list-content" key={item.id}>
                            <span> {item.id} </span>
                            <span> {item.name} </span>
                            <span> ￥{item.price} </span>
                            <span className="edit" onClick={() => this.showEditDialog(item.id,item.price)}> 编辑 </span>
                        </div>
                    )
                }
                <Modal title="修改商品的价格" visible={this.visible} onOk={()=>this.handleOk()} onCancel={this.handleCancel}>
                    {/* <Input value={currentPrice} onChange={this.inputChange}/> */}
                </Modal>
            </div>
        )
    }
}

const mapStatetoProps = state => {
    return {
      product: state.product
    }
}

const mapDispatchToProps = {
    getDataList
}

export default connect(mapStatetoProps, mapDispatchToProps)(List);
