import { combineReducers } from 'redux'
import city from './city'
import counter from './counter'

//将city和counter两个reducers合并在一起
//现在city这个reducer专门用来管理city数据
//counter这个reducer专门用来管理counter的数据
export default combineReducers({
    city,
    count:counter
})

/*
现在合并后的数据就变成这样了

{
  city:"无锡",
  count:0
}
*/
