/**
 * 初始化数据
 */
let obj = {
    currentList:[],//当前页的数据
    totalData: [],//请求到的全部数据
    current: 1, //当前页码
    pageSize: 10, //每页显示的条数
    total: 0,//商品列表总条数
}

export default function product(state = obj, action) {
    switch (action.type) {
        case "SETDATALIST":
            const {list, total} = action
            console.log(state,'iiiiiiiii')
            return {
                ...state,
                totalData:list,
                total:total,
                currentList : list.slice((state.current-1)*state.pageSize,state.current*state.pageSize)
            };
        case "GETLIST":
            const {pageNumber,pageSize} = state
            let result = state.totalData.filter(item => item.name.indexOf(action.name) > -1)
            result = result.slice((pageNumber-1)*pageSize,pageNumber*pageSize)
            return {
                ...state,
                currentList: result,
                total:result.length
            }
        default:
            return obj;
    }
}
