import axios from 'axios';

const SET_DATA_LIST = "SETDATALIST"

export function setDataList(list){
    return{
        type: SET_DATA_LIST,
        list
    }
}

export function getDataList() {
    return (dispatch,getState) =>{
        let result = axios.get('productList.json')
        result.then(res => {
            const data = res.data
            console.log(getState(),'hhhhhhhhhhh')
            const {pageSize,current} = getState().product
            const total = Math.ceil(data.length / pageSize)
            const currentList = data.slice((current-1)*pageSize,current*pageSize)
            console.log(total,currentList)
            dispatch(setDataList(data,total,currentList))
        })
    }
}
