let obj = {
    currentList:[],//当前页的数据
    totalData: [],//请求到的全部数据
    current: 1, //当前页码
    pageSize: 10, //每页显示的条数
    total: 0,//商品列表总条数
    currentId: 0,//要修改的当前商品的id
    currentPrice: 0,//要修改的当前商品的价格
    visible: false,//控制编辑框的显示和隐藏
}

export default function product(state = 0, action) {
    switch (action.type) {
        case "ADD":
            return state + action.step;
        case "MINUS":
            return state - 1;
        default:
            return state;
    }
}
